        return new Source("{% extends 'base.html.twig' %}

{% block title %}Troc'Eco | Ma Messagerie{% endblock %}

{# MAIN #}
{% block contenuPrincipal %}

<section>
    <div class=\"shadow-lg\">
        <h1 class=\"text-center py-3 my-5\">Ma Messagerie</h1>
    </div>
    
{# FORMULAIRE#}
    <div class=\"container shadow bg-vert\">
        <div>
            <h2>Mes messages</h2>
                <!-- Faire une boucle for  -->
                    {% for message in app.user.mesmessages %}
                    <div class=\"row g-0 border\">
                       
                        <div class=\"col-12 p-5\">
                            <div>
                                <h2 class=\"text-capitalize\">{{ message.destinataire }}</h2>
                                <p class=\"\">{{ message.message }}</p>
                            </div>
                        </div>
                    </div>
                    {% endfor %}
            
                
                
        </div>
    </div>
    
</section>

{% endblock %}", "/user/message/liste-message.html.twig", "/Users/bouhidjennifer/Desktop/troc-eco/TROC-ECO/templates/user/message/liste-message.html.twig");
